mapper.select:
SELECT COMPANY_NAME
  FROM T_COMPANY
   WHERE mem_id = #{mem_id}

mapper.insert:
{ call declare begin
INSERT INTO T_MEMBER(MEM_IDX, MEM_ID, MEM_PW, MEM_NAME, MEM_CATE, MEM_PHONE, MEM_REG_IP, MEM_REG_DT)
VALUES(MEM_SQ.NEXTVAL, #{mem_id}, #{mem_pw}, #{company_name}, 'c', #{company_telephone}, #{ip}, SYSDATE);
INSERT INTO T_COMPANY(ADDRESS, ADDRESS_EN, ADDR_DT_EN, ADDR_DT_KOR, BUSINESS_IMG, BUSINESS_INFO, BUSINESS_NUM, CATE_CHILD_EN, CATE_CHILD_KO, CATE_PRNT_EN, CATE_PRNT_KO, CEO_NAME, COMPANY_IDX, COMPANY_NAME, COMPANY_TELEPHONE, DOMITORY, DO_EN, DO_KOR, EMPLOYEE_NUM, GU_GUN_EUP_ENG, GU_GUN_EUP_KOR, HR_MANAGER, IP, MANAGER_EMAIL, MEALS, MEM_ID, REGDATE ) VALUES(#{address},#{address_en},#{addr_dt_en},#{addr_dt_kor}, #{business_img},#{business_info},#{business_num}, #{cate_child_en},#{cate_child_ko},#{cate_prnt_en},#{cate_prnt_ko}, #{ceo_name},COMP_SQ.NEXTVAL,#{company_name},#{company_telephone}, #{domitory},#{do_en},#{do_kor}, #{employee_num},#{gu_gun_eup_eng},#{gu_gun_eup_kor}, #{hr_manager},#{ip}, #{manager_email},#{meals},#{mem_id},SYSDATE ); end }
